use anyhow::{Result};
use gsc_crypt::{crypt_from_cipher, GscCryptTrait, new_passphrase_crypt};
use serde_json::Value;
use md5::{Digest, Md5};
pub struct AdvancedCrypt {
    share_key: String,
}

impl AdvancedCrypt {
    pub fn new(key: &str) -> Self {
        // 计算 key 的 md5 并转成 string
        let mut hasher = Md5::new();
        hasher.update(key);
        AdvancedCrypt {
            share_key: format!("{:x}", hasher.finalize()),
        }
    }

    pub fn encrypt(&self, data: Value) -> String {
        let v = serde_json::to_string(&data).unwrap();
        let str_bytes = v.as_bytes();
        let mut mc = new_passphrase_crypt!(self.share_key.as_str());
        let cipher_bytes = mc.encrypt_bytes_to_bytes(str_bytes);
        return  mc.signature(cipher_bytes)
    }

    pub fn decrypt(&self, cipher_base64: &str) -> Result<Value> {
        let (mc, cipher_bytes) = crypt_from_cipher!(self.share_key.as_str(), cipher_base64)?;
        let str_bytes = mc.decrypt_bytes_to_bytes(cipher_bytes.as_slice())?;
        serde_json::from_slice(&str_bytes).map_err(anyhow::Error::from)
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_decrypt() {
        let key = "VcAmGCISOnRc6AA";
        let encode_context = "";
        let coder = AdvancedCrypt::new(key);
        let decrypted = coder.decrypt(encode_context);
        assert!(decrypted.is_ok());
        let decrypted = decrypted.unwrap();
        // 判断 decrypted 是否包含非空 img 字段
        let img = decrypted.get("img");
        assert!(img.is_some());
        let img = img.unwrap();
        assert!(!img.to_string().is_empty());
    }
}